<?php

namespace Tests\Feature;

use Illuminate\Foundation\Testing\DatabaseTransactions;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Tests\TestCase;

use App\Models\User;
use App\Models\UserSpecialty;
use App\Models\UserGenre;
use App\Models\UserMood;
use App\Models\UserLocation;
use App\Models\Song;

use Illuminate\Support\Facades\Auth;

class ProfileControllerTest extends TestCase
{
    use DatabaseTransactions;

    public function test_profile_returns_correct_view_with_data()
    {
        // Create a test user
        $user = User::factory()->create();

        // Create test data associated with the user
        $specialties = UserSpecialty::factory()->count(3)->create(['user_id' => $user->id]);
        $genres = UserGenre::factory()->count(3)->create(['user_id' => $user->id]);
        $moods = UserMood::factory()->count(3)->create(['user_id' => $user->id]);
        $locations = UserLocation::factory()->count(3)->create(['user_id' => $user->id]);
        $songs = Song::factory()->count(3)->create(['user_id' => $user->id]);

        // Log in as the test user
        Auth::login($user);

        // Call the profile function
        $response = $this->get('/profile');

        // Assert that the response is successful
        $response->assertStatus(200);

        // Assert that the returned view is 'profile'
        $response->assertViewIs('profile');

        // Assert that the view contains the user data
        $response->assertSee('user', $user->name);
        $response->assertViewHas('user_specialties', $specialties);
        $response->assertViewHas('user_genres', $genres);
        $response->assertViewHas('user_moods', $moods);
        $response->assertViewHas('locations', $locations);
        $response->assertViewHas('songs', $songs);
    }

    public function test_profile_update_successfully()
    {
        $user = User::factory()->create();

        // Log in as the test user
        Auth::login($user);

        $base64 = "data:image/png;base64,";

        $response = $this->post('/profile', [
            'name' => 'John Doe',
            'email' => 'john@example.com',
            'status' => 'active',
            'description' => 'Updated description',
            'password' => 'newpassword',
            'password_confirmation' => 'newpassword',
            'specialties' => ['Specialty 1', 'Specialty 2'],
            'base64_image'  => $base64,
            'genres' => ['Genre 1', 'Genre 2'],
            'locations' => [
                ['county' => 'Klaipėda', 'city' => 'Palanga', 'address' => 'J. Basanavičiaus g. 1'],
                ['county' => 'Kaunas', 'city' => 'Kėdainiai', 'address' => 'J. Basanavičiaus g. 2'],
            ],
            'moods' => ['Ekscentriška', 'Naivi'],
            'songs' => [
                ['title' => 'Love Me Like You Do', 'original_url' => 'https://youtu.be/jezEIMRwVTg?si=GAT4sV2x-FIlYpxz', 'genres' => ['Rokas'], 'moods' => ['Nostalgiška']],
                ['title' => 'All My Exs Live In Texas', 'original_url' => 'https://www.youtube.com/watch?v=ZkXG3ZrXlbc', 'genres' => ['Kantri'], 'moods' => ['Laiminga']],
            ],
        ]);

        $response->assertRedirect(route('profile'));
        $response->assertSessionHas("success", "Profilis sėkmingai atnaujintas!");

        // Assert that user information is updated
        $this->assertDatabaseHas('users', [
            'name' => 'John Doe',
            'email' => 'john@example.com',
            'status' => 'active',
            'description' => 'Updated description',
        ]);

        // Assert that user specialties are updated
        $this->assertCount(2, $user->fresh()->specialties);

        // Assert that user genres are updated
        $this->assertCount(2, $user->fresh()->genres);

        // Assert that user locations are updated
        $this->assertCount(2, $user->fresh()->locations);

        // Assert that user moods are updated
        $this->assertCount(2, $user->fresh()->moods);

        // Assert that user songs are updated
        $this->assertCount(2, $user->fresh()->songs);
        $this->assertCount(1, $user->fresh()->songs[0]->genres);
        $this->assertCount(1, $user->fresh()->songs[0]->moods);
        $this->assertCount(1, $user->fresh()->songs[1]->genres);
        $this->assertCount(1, $user->fresh()->songs[1]->moods);
    }

    public function test_show_user_profile()
    {
        // Create a user
        $user = User::factory()->create();

        // Hit the route
        $response = $this->get('/profile/' . $user->id);

        // Check response status
        $response->assertStatus(200);

        // Check if the user's profile data is present in the view
        $response->assertViewHas('user', $user);
    }
}
